    root_child0.set_flex_direction(yoga.FLEX_DIRECTION_ROW)
    root_child0.set_align_content(yoga.ALIGN_STRETCH)
    root_child0.set_align_items(yoga.ALIGN_CENTER)
    root_child0.set_width(100)
    root_child0.set_height(100)
