#include <gtest/gtest.h>
#include <yoga/Yoga.h>
#include "../util/TestUtil.h"

TEST(YogaTest, absolute_correct_cross_child_size_with_percentage) {
  YGConfigRef config = YGConfigNew();

  YGNodeRef root = YGNodeNewWithConfig(config);
  YGNodeStyleSetWidth(root, 300);
  YGNodeStyleSetHeight(root, 110);
  YGNodeStyleSetDisplay(root, YGDisplayGrid);

  YGNodeRef root_child0 = YGNodeNewWithConfig(config);
  YGNodeStyleSetPositionType(root_child0, YGPositionTypeAbsolute);
  YGNodeStyleSetPosition(root_child0, YGEdgeTop, 40);
  YGNodeStyleSetPosition(root_child0, YGEdgeLeft, 50);
  YGNodeInsertChild(root, root_child0, 0);

  YGNodeRef root_child0_child0 = YGNodeNewWithConfig(config);
  YGNodeStyleSetWidth(root_child0_child0, 200);
  YGNodeStyleSetHeight(root_child0_child0, 10);
  YGNodeInsertChild(root_child0, root_child0_child0, 0);

  YGNodeRef root_child0_child1 = YGNodeNewWithConfig(config);
  YGNodeStyleSetWidthPercent(root_child0_child1, 100);
  YGNodeStyleSetHeight(root_child0_child1, 10);
  YGNodeInsertChild(root_child0, root_child0_child1, 1);

  YGNodeRef root_child0_child2 = YGNodeNewWithConfig(config);
  YGNodeStyleSetWidthPercent(root_child0_child2, 100);
  YGNodeStyleSetHeight(root_child0_child2, 10);
  YGNodeInsertChild(root_child0, root_child0_child2, 2);

  YGNodeRef root_child0_child2_child0 = YGNodeNewWithConfig(config);
  YGNodeStyleSetWidth(root_child0_child2_child0, 10);
  YGNodeStyleSetHeight(root_child0_child2_child0, 10);
  YGNodeInsertChild(root_child0_child2, root_child0_child2_child0, 0);
  YGNodeCalculateLayout(root, YGUndefined, YGUndefined, YGDirectionLTR);

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root));
  ASSERT_FLOAT_EQ(300, YGNodeLayoutGetWidth(root));
  ASSERT_FLOAT_EQ(110, YGNodeLayoutGetHeight(root));

  ASSERT_FLOAT_EQ(50, YGNodeLayoutGetLeft(root_child0));
  ASSERT_FLOAT_EQ(40, YGNodeLayoutGetTop(root_child0));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetWidth(root_child0));
  ASSERT_FLOAT_EQ(30, YGNodeLayoutGetHeight(root_child0));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0_child0));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetWidth(root_child0_child0));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetHeight(root_child0_child0));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0_child1));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetTop(root_child0_child1));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetWidth(root_child0_child1));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetHeight(root_child0_child1));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0_child2));
  ASSERT_FLOAT_EQ(20, YGNodeLayoutGetTop(root_child0_child2));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetWidth(root_child0_child2));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetHeight(root_child0_child2));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0_child2_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0_child2_child0));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetWidth(root_child0_child2_child0));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetHeight(root_child0_child2_child0));

  YGNodeCalculateLayout(root, YGUndefined, YGUndefined, YGDirectionRTL);

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root));
  ASSERT_FLOAT_EQ(300, YGNodeLayoutGetWidth(root));
  ASSERT_FLOAT_EQ(110, YGNodeLayoutGetHeight(root));

  ASSERT_FLOAT_EQ(50, YGNodeLayoutGetLeft(root_child0));
  ASSERT_FLOAT_EQ(40, YGNodeLayoutGetTop(root_child0));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetWidth(root_child0));
  ASSERT_FLOAT_EQ(30, YGNodeLayoutGetHeight(root_child0));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0_child0));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetWidth(root_child0_child0));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetHeight(root_child0_child0));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0_child1));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetTop(root_child0_child1));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetWidth(root_child0_child1));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetHeight(root_child0_child1));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0_child2));
  ASSERT_FLOAT_EQ(20, YGNodeLayoutGetTop(root_child0_child2));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetWidth(root_child0_child2));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetHeight(root_child0_child2));

  ASSERT_FLOAT_EQ(190, YGNodeLayoutGetLeft(root_child0_child2_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0_child2_child0));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetWidth(root_child0_child2_child0));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetHeight(root_child0_child2_child0));

  YGNodeFreeRecursive(root);

  YGConfigFree(config);
}
