#include <gtest/gtest.h>
#include <yoga/Yoga.h>
#include "../util/TestUtil.h"

TEST(YogaTest, grid_absolute_container_left_overrides_right) {
  YGConfigRef config = YGConfigNew();

  YGNodeRef root = YGNodeNewWithConfig(config);
  YGNodeStyleSetPositionType(root, YGPositionTypeAbsolute);
  YGNodeStyleSetDisplay(root, YGDisplayGrid);
  auto root_gridTemplateColumns = YGGridTrackListCreate();
  YGGridTrackListAddTrack(root_gridTemplateColumns, YGPoints(40));
  YGGridTrackListAddTrack(root_gridTemplateColumns, YGPoints(40));
  YGGridTrackListAddTrack(root_gridTemplateColumns, YGPoints(40));
  YGNodeStyleSetGridTemplateColumns(root, root_gridTemplateColumns);
  YGGridTrackListFree(root_gridTemplateColumns);
  auto root_gridTemplateRows = YGGridTrackListCreate();
  YGGridTrackListAddTrack(root_gridTemplateRows, YGPoints(40));
  YGGridTrackListAddTrack(root_gridTemplateRows, YGPoints(40));
  YGGridTrackListAddTrack(root_gridTemplateRows, YGPoints(40));
  YGNodeStyleSetGridTemplateRows(root, root_gridTemplateRows);
  YGGridTrackListFree(root_gridTemplateRows);
  YGNodeStyleSetPadding(root, YGEdgeTop, 10);
  YGNodeStyleSetPadding(root, YGEdgeRight, 20);
  YGNodeStyleSetPadding(root, YGEdgeBottom, 30);
  YGNodeStyleSetPadding(root, YGEdgeLeft, 40);

  YGNodeRef root_child0 = YGNodeNewWithConfig(config);
  YGNodeStyleSetPositionType(root_child0, YGPositionTypeAbsolute);
  YGNodeStyleSetPosition(root_child0, YGEdgeRight, 2);
  YGNodeStyleSetPosition(root_child0, YGEdgeLeft, 5);
  YGNodeStyleSetWidth(root_child0, 10);
  YGNodeInsertChild(root, root_child0, 0);
  YGNodeCalculateLayout(root, YGUndefined, YGUndefined, YGDirectionLTR);

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root));
  ASSERT_FLOAT_EQ(180, YGNodeLayoutGetWidth(root));
  ASSERT_FLOAT_EQ(160, YGNodeLayoutGetHeight(root));

  ASSERT_FLOAT_EQ(5, YGNodeLayoutGetLeft(root_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetWidth(root_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetHeight(root_child0));

  YGNodeCalculateLayout(root, YGUndefined, YGUndefined, YGDirectionRTL);

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root));
  ASSERT_FLOAT_EQ(180, YGNodeLayoutGetWidth(root));
  ASSERT_FLOAT_EQ(160, YGNodeLayoutGetHeight(root));

  ASSERT_FLOAT_EQ(168, YGNodeLayoutGetLeft(root_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0));
  ASSERT_FLOAT_EQ(10, YGNodeLayoutGetWidth(root_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetHeight(root_child0));

  YGNodeFreeRecursive(root);

  YGConfigFree(config);
}
