#include <gtest/gtest.h>
#include <yoga/Yoga.h>
#include "../util/TestUtil.h"

TEST(YogaTest, grid_expand_flexible_indefinite_free_space_container) {
  YGConfigRef config = YGConfigNew();

  YGNodeRef root = YGNodeNewWithConfig(config);
  YGNodeStyleSetPositionType(root, YGPositionTypeAbsolute);
  YGNodeStyleSetWidthMaxContent(root);

  YGNodeRef root_child0 = YGNodeNewWithConfig(config);
  YGNodeStyleSetDisplay(root_child0, YGDisplayGrid);
  auto root_child0_gridTemplateColumns = YGGridTrackListCreate();
  YGGridTrackListAddTrack(root_child0_gridTemplateColumns, YGFr(1));
  YGGridTrackListAddTrack(root_child0_gridTemplateColumns, YGFr(2));
  YGGridTrackListAddTrack(root_child0_gridTemplateColumns, YGFr(1));
  YGNodeStyleSetGridTemplateColumns(root_child0, root_child0_gridTemplateColumns);
  YGGridTrackListFree(root_child0_gridTemplateColumns);
  auto root_child0_gridTemplateRows = YGGridTrackListCreate();
  YGGridTrackListAddTrack(root_child0_gridTemplateRows, YGFr(1));
  YGGridTrackListAddTrack(root_child0_gridTemplateRows, YGFr(2));
  YGNodeStyleSetGridTemplateRows(root_child0, root_child0_gridTemplateRows);
  YGGridTrackListFree(root_child0_gridTemplateRows);
  YGNodeInsertChild(root, root_child0, 0);

  YGNodeRef root_child0_child0 = YGNodeNewWithConfig(config);
  YGNodeStyleSetWidth(root_child0_child0, 100);
  YGNodeStyleSetHeight(root_child0_child0, 80);
  YGNodeInsertChild(root_child0, root_child0_child0, 0);

  YGNodeRef root_child0_child1 = YGNodeNewWithConfig(config);
  YGNodeStyleSetWidth(root_child0_child1, 150);
  YGNodeStyleSetHeight(root_child0_child1, 120);
  YGNodeInsertChild(root_child0, root_child0_child1, 1);

  YGNodeRef root_child0_child2 = YGNodeNewWithConfig(config);
  YGNodeStyleSetWidth(root_child0_child2, 120);
  YGNodeStyleSetHeight(root_child0_child2, 100);
  YGNodeInsertChild(root_child0, root_child0_child2, 2);
  YGNodeCalculateLayout(root, YGUndefined, YGUndefined, YGDirectionLTR);

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root));
  ASSERT_FLOAT_EQ(480, YGNodeLayoutGetWidth(root));
  ASSERT_FLOAT_EQ(360, YGNodeLayoutGetHeight(root));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0));
  ASSERT_FLOAT_EQ(480, YGNodeLayoutGetWidth(root_child0));
  ASSERT_FLOAT_EQ(360, YGNodeLayoutGetHeight(root_child0));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0_child0));
  ASSERT_FLOAT_EQ(100, YGNodeLayoutGetWidth(root_child0_child0));
  ASSERT_FLOAT_EQ(80, YGNodeLayoutGetHeight(root_child0_child0));

  ASSERT_FLOAT_EQ(120, YGNodeLayoutGetLeft(root_child0_child1));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0_child1));
  ASSERT_FLOAT_EQ(150, YGNodeLayoutGetWidth(root_child0_child1));
  ASSERT_FLOAT_EQ(120, YGNodeLayoutGetHeight(root_child0_child1));

  ASSERT_FLOAT_EQ(360, YGNodeLayoutGetLeft(root_child0_child2));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0_child2));
  ASSERT_FLOAT_EQ(120, YGNodeLayoutGetWidth(root_child0_child2));
  ASSERT_FLOAT_EQ(100, YGNodeLayoutGetHeight(root_child0_child2));

  YGNodeCalculateLayout(root, YGUndefined, YGUndefined, YGDirectionRTL);

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root));
  ASSERT_FLOAT_EQ(480, YGNodeLayoutGetWidth(root));
  ASSERT_FLOAT_EQ(360, YGNodeLayoutGetHeight(root));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0));
  ASSERT_FLOAT_EQ(480, YGNodeLayoutGetWidth(root_child0));
  ASSERT_FLOAT_EQ(360, YGNodeLayoutGetHeight(root_child0));

  ASSERT_FLOAT_EQ(380, YGNodeLayoutGetLeft(root_child0_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0_child0));
  ASSERT_FLOAT_EQ(100, YGNodeLayoutGetWidth(root_child0_child0));
  ASSERT_FLOAT_EQ(80, YGNodeLayoutGetHeight(root_child0_child0));

  ASSERT_FLOAT_EQ(210, YGNodeLayoutGetLeft(root_child0_child1));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0_child1));
  ASSERT_FLOAT_EQ(150, YGNodeLayoutGetWidth(root_child0_child1));
  ASSERT_FLOAT_EQ(120, YGNodeLayoutGetHeight(root_child0_child1));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0_child2));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0_child2));
  ASSERT_FLOAT_EQ(120, YGNodeLayoutGetWidth(root_child0_child2));
  ASSERT_FLOAT_EQ(100, YGNodeLayoutGetHeight(root_child0_child2));

  YGNodeFreeRecursive(root);

  YGConfigFree(config);
}
