  ASSERT_FLOAT_EQ(40, YGNodeLayoutGetHeight(root_child0_child3));

  ASSERT_FLOAT_EQ(60, YGNodeLayoutGetLeft(root_child0_child4));
  ASSERT_FLOAT_EQ(40, YGNodeLayoutGetTop(root_child0_child4));
  ASSERT_FLOAT_EQ(60, YGNodeLayoutGetWidth(root_child0_child4));
  ASSERT_FLOAT_EQ(40, YGNodeLayoutGetHeight(root_child0_child4));

  YGNodeCalculateLayout(root, YGUndefined, YGUndefined, YGDirectionRTL);

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root));
  ASSERT_FLOAT_EQ(120, YGNodeLayoutGetWidth(root));
  ASSERT_FLOAT_EQ(80, YGNodeLayoutGetHeight(root));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0));
