  YGNodeStyleSetMarginPercent(root_child0, YGEdgeTop, 5);
  YGNodeStyleSetMarginPercent(root_child0, YGEdgeRight, 10);
  YGNodeStyleSetMarginPercent(root_child0, YGEdgeBottom, 15);
  YGNodeStyleSetMarginPercent(root_child0, YGEdgeLeft, 20);
