  YGNodeStyleSetDisplay(root_child0, YGDisplayGrid);
  auto root_child0_gridTemplateColumns = YGGridTrackListCreate();
  YGGridTrackListAddTrack(root_child0_gridTemplateColumns, YGMinMax(YGAuto(), YGPercent(20)));
  YGNodeStyleSetGridTemplateColumns(root_child0, root_child0_gridTemplateColumns);
  YGGridTrackListFree(root_child0_gridTemplateColumns);
  auto root_child0_gridTemplateRows = YGGridTrackListCreate();
  YGGridTrackListAddTrack(root_child0_gridTemplateRows, YGPoints(40));
  YGNodeStyleSetGridTemplateRows(root_child0, root_child0_gridTemplateRows);
  YGGridTrackListFree(root_child0_gridTemplateRows);
  YGNodeInsertChild(root, root_child0, 0);

  YGNodeRef root_child0_child0 = YGNodeNewWithConfig(config);
  YGNodeInsertChild(root_child0, root_child0_child0, 0);
  YGNodeSetContext(root_child0_child0, (void*)"HH HH");
  YGNodeSetMeasureFunc(root_child0_child0, &facebook::yoga::test::IntrinsicSizeMeasure);
