#include <gtest/gtest.h>
#include <yoga/Yoga.h>
#include "../util/TestUtil.h"

TEST(YogaTest, grid_padding_border_overrides_min_size) {
  YGConfigRef config = YGConfigNew();

  YGNodeRef root = YGNodeNewWithConfig(config);
  YGNodeStyleSetPositionType(root, YGPositionTypeAbsolute);
  YGNodeStyleSetDisplay(root, YGDisplayGrid);

  YGNodeRef root_child0 = YGNodeNewWithConfig(config);
  YGNodeStyleSetPadding(root_child0, YGEdgeTop, 2);
  YGNodeStyleSetPadding(root_child0, YGEdgeRight, 4);
  YGNodeStyleSetPadding(root_child0, YGEdgeBottom, 6);
  YGNodeStyleSetPadding(root_child0, YGEdgeLeft, 8);
  YGNodeStyleSetBorder(root_child0, YGEdgeTop, 1);
  YGNodeStyleSetBorder(root_child0, YGEdgeRight, 3);
  YGNodeStyleSetBorder(root_child0, YGEdgeBottom, 5);
  YGNodeStyleSetBorder(root_child0, YGEdgeLeft, 7);
  YGNodeInsertChild(root, root_child0, 0);
  YGNodeCalculateLayout(root, YGUndefined, YGUndefined, YGDirectionLTR);

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root));
  ASSERT_FLOAT_EQ(22, YGNodeLayoutGetWidth(root));
  ASSERT_FLOAT_EQ(14, YGNodeLayoutGetHeight(root));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0));
  ASSERT_FLOAT_EQ(22, YGNodeLayoutGetWidth(root_child0));
  ASSERT_FLOAT_EQ(14, YGNodeLayoutGetHeight(root_child0));

  YGNodeCalculateLayout(root, YGUndefined, YGUndefined, YGDirectionRTL);

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root));
  ASSERT_FLOAT_EQ(22, YGNodeLayoutGetWidth(root));
  ASSERT_FLOAT_EQ(14, YGNodeLayoutGetHeight(root));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child0));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root_child0));
  ASSERT_FLOAT_EQ(22, YGNodeLayoutGetWidth(root_child0));
  ASSERT_FLOAT_EQ(14, YGNodeLayoutGetHeight(root_child0));

  YGNodeFreeRecursive(root);

  YGConfigFree(config);
}
