#include <gtest/gtest.h>
#include <yoga/Yoga.h>
#include "../util/TestUtil.h"

TEST(YogaTest, grid_percent_items_nested_moderate) {
  YGConfigRef config = YGConfigNew();

  YGNodeRef root = YGNodeNewWithConfig(config);
  YGNodeStyleSetPositionType(root, YGPositionTypeAbsolute);
  YGNodeStyleSetPadding(root, YGEdgeLeft, 3);
  YGNodeStyleSetPadding(root, YGEdgeTop, 3);
  YGNodeStyleSetPadding(root, YGEdgeRight, 3);
  YGNodeStyleSetPadding(root, YGEdgeBottom, 3);
  YGNodeStyleSetWidth(root, 200);
  YGNodeStyleSetDisplay(root, YGDisplayGrid);

  YGNodeRef root_child0 = YGNodeNewWithConfig(config);
  YGNodeStyleSetMargin(root_child0, YGEdgeLeft, 5);
  YGNodeStyleSetMargin(root_child0, YGEdgeTop, 5);
  YGNodeStyleSetMargin(root_child0, YGEdgeRight, 5);
  YGNodeStyleSetMargin(root_child0, YGEdgeBottom, 5);
  YGNodeStyleSetPaddingPercent(root_child0, YGEdgeLeft, 3);
  YGNodeStyleSetPaddingPercent(root_child0, YGEdgeTop, 3);
  YGNodeStyleSetPaddingPercent(root_child0, YGEdgeRight, 3);
  YGNodeStyleSetPaddingPercent(root_child0, YGEdgeBottom, 3);
  YGNodeStyleSetWidthPercent(root_child0, 50);
  YGNodeStyleSetDisplay(root_child0, YGDisplayGrid);
  YGNodeInsertChild(root, root_child0, 0);

  YGNodeRef root_child0_child0 = YGNodeNewWithConfig(config);
  YGNodeStyleSetMarginPercent(root_child0_child0, YGEdgeLeft, 5);
  YGNodeStyleSetMarginPercent(root_child0_child0, YGEdgeTop, 5);
  YGNodeStyleSetMarginPercent(root_child0_child0, YGEdgeRight, 5);
  YGNodeStyleSetMarginPercent(root_child0_child0, YGEdgeBottom, 5);
  YGNodeStyleSetPadding(root_child0_child0, YGEdgeLeft, 3);
  YGNodeStyleSetPadding(root_child0_child0, YGEdgeTop, 3);
  YGNodeStyleSetPadding(root_child0_child0, YGEdgeRight, 3);
  YGNodeStyleSetPadding(root_child0_child0, YGEdgeBottom, 3);
  YGNodeStyleSetWidthPercent(root_child0_child0, 45);
  YGNodeInsertChild(root_child0, root_child0_child0, 0);
  YGNodeCalculateLayout(root, YGUndefined, YGUndefined, YGDirectionLTR);

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetWidth(root));
  ASSERT_FLOAT_EQ(24, YGNodeLayoutGetHeight(root));

  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetLeft(root_child0));
  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetTop(root_child0));
  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetWidth(root_child0));
  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetHeight(root_child0));

  ASSERT_FLOAT_EQ(1, YGNodeLayoutGetLeft(root_child0_child0));
  ASSERT_FLOAT_EQ(1, YGNodeLayoutGetTop(root_child0_child0));
  ASSERT_FLOAT_EQ(6, YGNodeLayoutGetWidth(root_child0_child0));
  ASSERT_FLOAT_EQ(6, YGNodeLayoutGetHeight(root_child0_child0));

  YGNodeCalculateLayout(root, YGUndefined, YGUndefined, YGDirectionRTL);

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetWidth(root));
  ASSERT_FLOAT_EQ(24, YGNodeLayoutGetHeight(root));

  ASSERT_FLOAT_EQ(184, YGNodeLayoutGetLeft(root_child0));
  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetTop(root_child0));
  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetWidth(root_child0));
  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetHeight(root_child0));

  ASSERT_FLOAT_EQ(1, YGNodeLayoutGetLeft(root_child0_child0));
  ASSERT_FLOAT_EQ(1, YGNodeLayoutGetTop(root_child0_child0));
  ASSERT_FLOAT_EQ(6, YGNodeLayoutGetWidth(root_child0_child0));
  ASSERT_FLOAT_EQ(6, YGNodeLayoutGetHeight(root_child0_child0));

  YGNodeFreeRecursive(root);

  YGConfigFree(config);
}
