#include <gtest/gtest.h>
#include <yoga/Yoga.h>
#include "../util/TestUtil.h"

TEST(YogaTest, grid_percent_items_nested_with_padding_margin) {
  YGConfigRef config = YGConfigNew();

  YGNodeRef root = YGNodeNewWithConfig(config);
  YGNodeStyleSetPositionType(root, YGPositionTypeAbsolute);
  YGNodeStyleSetDisplay(root, YGDisplayGrid);
  YGNodeStyleSetWidth(root, 200);
  YGNodeStyleSetHeight(root, 200);
  auto root_gridTemplateRows = YGGridTrackListCreate();
  YGGridTrackListAddTrack(root_gridTemplateRows, YGFr(1));
  YGGridTrackListAddTrack(root_gridTemplateRows, YGFr(4));
  YGNodeStyleSetGridTemplateRows(root, root_gridTemplateRows);
  YGGridTrackListFree(root_gridTemplateRows);

  YGNodeRef root_child0 = YGNodeNewWithConfig(config);
  YGNodeStyleSetDisplay(root_child0, YGDisplayGrid);
  YGNodeStyleSetMinWidthPercent(root_child0, 60);
  YGNodeStyleSetMargin(root_child0, YGEdgeAll, 5);
  YGNodeStyleSetPadding(root_child0, YGEdgeAll, 3);
  YGNodeInsertChild(root, root_child0, 0);

  YGNodeRef root_child0_child0 = YGNodeNewWithConfig(config);
  YGNodeStyleSetDisplay(root_child0_child0, YGDisplayGrid);
  YGNodeStyleSetWidthPercent(root_child0_child0, 50);
  YGNodeStyleSetMargin(root_child0_child0, YGEdgeAll, 5);
  YGNodeStyleSetPaddingPercent(root_child0_child0, YGEdgeAll, 3);
  YGNodeInsertChild(root_child0, root_child0_child0, 0);

  YGNodeRef root_child0_child0_child0 = YGNodeNewWithConfig(config);
  YGNodeStyleSetWidthPercent(root_child0_child0_child0, 45);
  YGNodeStyleSetMarginPercent(root_child0_child0_child0, YGEdgeAll, 5);
  YGNodeStyleSetPadding(root_child0_child0_child0, YGEdgeAll, 3);
  YGNodeInsertChild(root_child0_child0, root_child0_child0_child0, 0);

  YGNodeRef root_child1 = YGNodeNewWithConfig(config);
  YGNodeInsertChild(root, root_child1, 1);
  YGNodeCalculateLayout(root, YGUndefined, YGUndefined, YGDirectionLTR);

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetWidth(root));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetHeight(root));

  ASSERT_FLOAT_EQ(5, YGNodeLayoutGetLeft(root_child0));
  ASSERT_FLOAT_EQ(5, YGNodeLayoutGetTop(root_child0));
  ASSERT_FLOAT_EQ(22, YGNodeLayoutGetWidth(root_child0));
  ASSERT_FLOAT_EQ(30, YGNodeLayoutGetHeight(root_child0));

  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetLeft(root_child0_child0));
  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetTop(root_child0_child0));
  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetWidth(root_child0_child0));
  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetHeight(root_child0_child0));

  ASSERT_FLOAT_EQ(1, YGNodeLayoutGetLeft(root_child0_child0_child0));
  ASSERT_FLOAT_EQ(1, YGNodeLayoutGetTop(root_child0_child0_child0));
  ASSERT_FLOAT_EQ(6, YGNodeLayoutGetWidth(root_child0_child0_child0));
  ASSERT_FLOAT_EQ(6, YGNodeLayoutGetHeight(root_child0_child0_child0));

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root_child1));
  ASSERT_FLOAT_EQ(40, YGNodeLayoutGetTop(root_child1));
  ASSERT_FLOAT_EQ(32, YGNodeLayoutGetWidth(root_child1));
  ASSERT_FLOAT_EQ(160, YGNodeLayoutGetHeight(root_child1));

  YGNodeCalculateLayout(root, YGUndefined, YGUndefined, YGDirectionRTL);

  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetLeft(root));
  ASSERT_FLOAT_EQ(0, YGNodeLayoutGetTop(root));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetWidth(root));
  ASSERT_FLOAT_EQ(200, YGNodeLayoutGetHeight(root));

  ASSERT_FLOAT_EQ(173, YGNodeLayoutGetLeft(root_child0));
  ASSERT_FLOAT_EQ(5, YGNodeLayoutGetTop(root_child0));
  ASSERT_FLOAT_EQ(22, YGNodeLayoutGetWidth(root_child0));
  ASSERT_FLOAT_EQ(30, YGNodeLayoutGetHeight(root_child0));

  ASSERT_FLOAT_EQ(6, YGNodeLayoutGetLeft(root_child0_child0));
  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetTop(root_child0_child0));
  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetWidth(root_child0_child0));
  ASSERT_FLOAT_EQ(8, YGNodeLayoutGetHeight(root_child0_child0));

  ASSERT_FLOAT_EQ(1, YGNodeLayoutGetLeft(root_child0_child0_child0));
  ASSERT_FLOAT_EQ(1, YGNodeLayoutGetTop(root_child0_child0_child0));
  ASSERT_FLOAT_EQ(6, YGNodeLayoutGetWidth(root_child0_child0_child0));
  ASSERT_FLOAT_EQ(6, YGNodeLayoutGetHeight(root_child0_child0_child0));

  ASSERT_FLOAT_EQ(168, YGNodeLayoutGetLeft(root_child1));
  ASSERT_FLOAT_EQ(40, YGNodeLayoutGetTop(root_child1));
  ASSERT_FLOAT_EQ(32, YGNodeLayoutGetWidth(root_child1));
  ASSERT_FLOAT_EQ(160, YGNodeLayoutGetHeight(root_child1));

  YGNodeFreeRecursive(root);

  YGConfigFree(config);
}
