  YGNodeStyleSetWidth(root_child1, 40);
  YGNodeStyleSetHeight(root_child1, 30);
  YGNodeStyleSetGridRowStart(root_child1, 1);
  YGNodeStyleSetGridRowEndSpan(root_child1, 2);
  YGNodeStyleSetGridColumnStart(root_child1, 2);
  YGNodeStyleSetGridColumnEndSpan(root_child1, 2);
  YGNodeInsertChild(root, root_child1, 1);

  YGNodeRef root_child2 = YGNodeNewWithConfig(config);
